define([
    'Backbone',
    'Underscore',
    'common',
    'Validation',
    'moment',
    'constants'
], function (Backbone, _, common, Validation, moment, CONSTANTS) {
    'use strict';
    var ApplicationModel = Backbone.Model.extend({
        idAttribute: '_id',
        initialize : function () {
            this.on('invalid', function (model, errors) {
                var msg;

                if (errors.length > 0) {
                    msg = errors.join('\n');

                    App.render({
                        type   : 'error',
                        message: msg
                    });
                }

                model.unset('isHire', {silent: true});
            });
        },

        parse: function (response) {
            if (!response.data) {
                if (response.creationDate) {
                    response.creationDate = moment(response.creationDate).format('DD MMM, YYYY');
                }
                if (response.nextAction) {
                    response.nextAction = moment(response.nextAction).format('DD MMM, YYYY');
                }
                if (response.dateBirth) {
                    response.dateBirth = moment(response.dateBirth).format('DD MMM, YYYY');
                }
                if (response.createdBy) {
                    response.createdBy.date = moment(response.createdBy.date).format('DD MMM, YYYY');
                }

                if (response.editedBy) {
                    response.editedBy.date = moment(response.editedBy.date).format('DD MMM, YYYY');
                }

                if (response.notes) {
                    _.map(response.notes, function (note) {
                        note.date = moment(note.date)//.format('DD MMM, YYYY, H:mm:ss');
                        return note;
                    });
                }

                if (response.attachments) {
                    _.map(response.attachments, function (attachment) {
                        attachment.uploadDate = moment(attachment.uploadDate).format('DD MMM, YYYY, H:mm:ss');
                        return attachment;
                    });
                }

                if (response.transfer) {
                    response.transfer = _.map(response.transfer, function (transfer) {
                        transfer.date = common.utcDateToLocaleDate(transfer.date);
                        return transfer;
                    });
                }
            }
            return response;
        },

        validate: function (attrs) {
            var errors = [];
            Validation.checkNameField(errors, true, attrs.employmentType, 'Personal Information / Employment type');
            Validation.checkNameField(errors, true, attrs.name.first, 'First name');
            Validation.checkNameField(errors, true, attrs.name.last, 'Last name');
            Validation.checkEmailField(errors, true, attrs.personalEmail, 'Email');
            Validation.checkDateField(errors, true, attrs.dateBirth, 'Date of Birth');
            if (attrs.department) {
                Validation.checkGroupsNameField(errors, true, attrs.department._id || attrs.department, 'Department');
            }
            if (attrs.isEmployee) {
                Validation.checkGroupsNameField(errors, true, attrs.jobType, 'Job type');
                Validation.checkGroupsNameField(errors, true, attrs.manager, 'Manager');
                Validation.checkEmailField(errors, true, attrs.workEmail, 'Work Email');
                Validation.checkJobPositionField(errors, true, attrs.jobPosition, 'Job position');
                Validation.checkGroupsNameField(errors, true, attrs.department, 'Department');
            }
            Validation.checkPhoneField(errors, false, attrs.workPhones.phone, 'Phone');
            Validation.checkPhoneField(errors, false, attrs.workPhones.mobile, 'Mobile');
            Validation.checkMoneyField(errors, false, attrs.expectedSalary, 'Expected salary');
            Validation.checkMoneyField(errors, false, attrs.proposedSalary, 'Proposed salary');
            Validation.checkGroupsNameField(errors, true, attrs.gender, 'Personal Information/Gender');
            Validation.checkGroupsNameField(errors, true, attrs.marital, 'Personal Information/Marital status');
            if (errors.length > 0) {
                return errors;
            }
        },

        defaults: {
            isEmployee: false,
            imageSrc  : 'data:image/png;base64,',
            subject   : '',
            name      : {
                first: 'New',
                last : 'Application'
            },

            tags         : [],
            personalEmail: '',
            workPhones   : {
                mobile: '',
                phone : ''
            },

            relatedUser: null,
            department : {
                id  : '',
                name: ''
            },

            jobPosition: {
                id  : '',
                name: ''
            },

            nextAction    : null,
            source        : '',
            referredBy    : '',
            expectedSalary: 0,
            proposedSalary: 0,
            otherInfo     : '',
            workflow      : {
                wName : 'application',
                name  : 'Initial Qualification',
                status: 'New'
            },
            notes         : []
        },

        urlRoot: function () {
            return CONSTANTS.URLS.APPLICATIONS;
        }
    });
    return ApplicationModel;
});
